import React from 'react'
import PropTypes from 'prop-types'

import doExcludeAttribute from './doExcludeAttribute'
import getClassname from './getClassname'

var classAttributes = [
	'xs',
	'sm',
	'md',
	'lg',
	'xl',
	'xs-offset',
	'sm-offset',
	'md-offset',
	'lg-offset',
	'xl-offset',
]

var Col = (props) => {
	var attributes = doExcludeAttribute(props, classAttributes)
	attributes.className = getClassname(props, classAttributes)
	attributes.style = props.style

	return React.createElement(
		'div',
		attributes,
		(props.children || null)
	)
}

Col.propTypes = {
	xs: PropTypes.number,
	sm: PropTypes.number,
	md: PropTypes.number,
	lg: PropTypes.number,
	xl: PropTypes.number,
	className: PropTypes.string
}

Col.defaultProps = {
	className: ''
}

export default Col
