import React from 'react'
import PropTypes from 'prop-types'

import doExcludeAttribute from './doExcludeAttribute'
import getClassname from './getClassname'

var classAttributes = []

const Container = (props) => {
	var attributes = doExcludeAttribute(props, classAttributes)
	attributes.className = getClassname(props, classAttributes)
	attributes.style = props.style

	attributes.className += props.fluid ? ' container-fluid' : ' container'

	return React.createElement(
		'div',
		attributes,
		props.children
	)
}

Container.propTypes = {
	fluid: PropTypes.bool,
	className: PropTypes.string,
}

Container.defaultProps = {
	className: ''
}

export default Container
