import React from 'react'
import PropTypes from 'prop-types'

import doExcludeAttribute from './doExcludeAttribute'
import getClassname from './getClassname'

var classAttributes = []

const Row = (props) => {
	var attributes = doExcludeAttribute(props, classAttributes)
	attributes.className = getClassname(props, classAttributes) + ' row'
	attributes.style = props.style

	return React.createElement(
		'div',
		attributes,
		props.children
	)
}

Row.propTypes = {
	className: PropTypes.string,
}

Row.defaultProps = {
	className: ''
}

export default Row
