var doExcludeAttribute = (props, classAttributes) => {
	var excludeAttribute = [
		'className',
		'style',
		'children'
	].concat(classAttributes)

	var attributes = Object.assign({}, props)

	for (var i = 0, l = excludeAttribute.length; i < l; i++) {
		(attributes[excludeAttribute[i]] && delete attributes[excludeAttribute[i]])
	}

	return attributes
}

export default doExcludeAttribute
