var getClassname = (props, classAttributes) => {
	var className = (props.className || '').split(' ')

	for (var i = 0, l = classAttributes.length; i < l; i++) {
		if (props[classAttributes[i]]) {
			className.push('col-' + classAttributes[i] + '-' + props[classAttributes[i]])
		}
	}

	return className.join(' ')
}

export default getClassname
